import React from 'react';
import renderWithTheme from '../../../testUtils/renderWithTheme';

import Badge from '..';

describe('rendering', () => {
  it('renders badge content', () => {
    const { queryByText } = renderWithTheme(<Badge content={99} />);

    expect(queryByText('99')).toBeInTheDocument();
  });

  it('renders overflow number', () => {
    const { queryByText } = renderWithTheme(<Badge content={100} />);

    expect(queryByText('99+')).toBeInTheDocument();
  });

  it.each`
    variant       | intent
    ${'filled'}   | ${'basic'}
    ${'outlined'} | ${'basic'}
    ${'filled'}   | ${'primary'}
    ${'outlined'} | ${'primary'}
    ${'filled'}   | ${'success'}
    ${'outlined'} | ${'success'}
    ${'filled'}   | ${'danger'}
    ${'outlined'} | ${'danger'}
    ${'filled'}   | ${'warning'}
    ${'outlined'} | ${'warning'}
    ${'filled'}   | ${'error'}
    ${'outlined'} | ${'error'}
    ${'filled'}   | ${'archived'}
    ${'outlined'} | ${'archived'}
  `(
    'renders correctly with $variant variant and $intent intent',
    ({ variant, intent }) => {
      const { asFragment } = renderWithTheme(
        <Badge variant={variant} intent={intent} content="Badge" />
      );

      expect(asFragment()).toMatchSnapshot();
    }
  );
});
