import { isNumber, getPaddingState } from '../utils';

describe('isNumber', () => {
  it.each`
    content  | result
    ${12}    | ${true}
    ${'ABC'} | ${false}
    ${'123'} | ${false}
  `('returns $result when content is $content ', ({ content, result }) => {
    expect(isNumber(content)).toEqual(result);
  });
});

describe('getPaddingState', () => {
  it.each`
    content | state
    ${8}    | ${'narrowContent'}
    ${12}   | ${'wideContent'}
  `('returns $state when placement is $placement', ({ content, state }) => {
    expect(getPaddingState(content)).toEqual(state);
  });
});
