import React from 'react';

import Box from '../index';
import renderWithTheme from '../../../testUtils/renderWithTheme';

describe('rendering', () => {
  it('renders a Box container and its children', () => {
    const { getByText, getByTestId } = renderWithTheme(
      <Box data-test-id="box" bgColor="grey-light-90">
        Box content
      </Box>
    );

    expect(getByTestId('box')).toBeInTheDocument();
    expect(getByText('Box content')).toBeInTheDocument();
  });

  it('renders as HTML tag', () => {
    const { container } = renderWithTheme(<Box tagName="b">Bold</Box>);

    expect(container.firstChild?.nodeName).toEqual('B');
  });
});

describe('accessibility', () => {
  it('supports ARIA props', () => {
    const { container } = renderWithTheme(
      <Box aria-label="Aria label">Box content</Box>
    );

    expect(container.firstChild).toHaveAccessibleName('Aria label');
  });
});
