import React, { ReactNode } from 'react';
import type { ReactElement } from 'react';
import { useCss } from '../../utils/hooks';

import { StyledCardHeader } from './StyledCard';
import CardContext from './CardContext';
import type { CommonProps } from '../common';
import type { CardContextType } from './CardContext';

export interface CardHeaderProps extends CommonProps {
  /**
   * Custom content.
   */
  children?: ReactNode;
  /**
   * Short hand for Header content.
   */
  content?: ReactNode;
}

const CardHeader = ({
  content,
  children,
  id,
  className,
  style,
  sx = {},
  'data-test-id': dataTestId,
}: CardHeaderProps): ReactElement => {
  const { variant, size } = React.useContext(CardContext) as CardContextType;

  return (
    <StyledCardHeader
      themeVariant={variant}
      themeSize={size}
      id={id}
      className={className}
      style={{ ...style, ...useCss(sx) }}
      data-test-id={dataTestId}
    >
      {children !== undefined ? children : content}
    </StyledCardHeader>
  );
};

export default CardHeader;
