import React from 'react';
import renderWithTheme from '../../../testUtils/renderWithTheme';

import CardContent from '../CardContent';
import Card from '..';

describe('rendering', () => {
  it('renders card content', () => {
    const { getByText } = renderWithTheme(
      <Card>
        <CardContent content="Content of the card" />
      </Card>
    );
    expect(getByText('Content of the card')).toBeInTheDocument();
  });

  it('renders custom card content', () => {
    const { getByText } = renderWithTheme(
      <Card>
        <CardContent>
          <p>Custom card content</p>
        </CardContent>
      </Card>
    );
    expect(getByText('Custom card content')).toBeInTheDocument();
  });
});
