import React from 'react';
import { StyledLegendCircle } from '../../Legend/StyledLegend';
import Typography from '../../../Typography';

type DefaultTooltipProps = {
  label?: string;
  value?: number;
  legendColor?: string;
  percent?: string;
};

const DefaultTooltip = ({
  label,
  value,
  legendColor,
  percent,
}: DefaultTooltipProps) => {
  const hasPercent = percent !== undefined;
  const percentValue = hasPercent ? `(${percent}%)` : '';
  return (
    <>
      {!!legendColor && (
        <StyledLegendCircle
          data-test-id="legend-circle"
          themeColor={legendColor}
        />
      )}
      {label && (
        <Typography.Text
          fontSize={14}
          fontWeight="light"
          intent="subdued"
          tagName="span"
        >
          {label}
        </Typography.Text>
      )}
      {!!value && (
        <Typography.Text fontSize={14} fontWeight="light" tagName="span">
          {value}
        </Typography.Text>
      )}
      {hasPercent && (
        <Typography.Text fontSize={14} fontWeight="light" tagName="span">
          {percentValue}
        </Typography.Text>
      )}
    </>
  );
};

export default DefaultTooltip;
