import React from 'react';
import {
  StyledNavigateButton,
  StyledNavigateButtonWrapper,
} from '../StyledBarChart';
import { NavigationConfig } from '../../type';
import { CommonProps } from '../../../common';

interface NavigationProps extends CommonProps {
  top: number;
  left: number;
  onClickNavigation?: NavigationConfig['onNavigate'];
  disabledButtons?: NavigationConfig['disabledButtons'];
}

const Navigation = ({
  onClickNavigation,
  top,
  left,
  'data-test-id': dataTestId,
  disabledButtons,
}: NavigationProps) => {
  return (
    <>
      <StyledNavigateButtonWrapper
        themeTop={top}
        themePosition="left"
        themeLeft={left}
      >
        <StyledNavigateButton
          size="large"
          icon="single-left-arrow"
          intent="text"
          onClick={() => onClickNavigation?.('prev')}
          data-test-id={
            dataTestId ? `${dataTestId}-navigate-button-prev` : undefined
          }
          disabled={disabledButtons === 'prev'}
        />
      </StyledNavigateButtonWrapper>
      <StyledNavigateButtonWrapper themePosition="right" themeTop={top}>
        <StyledNavigateButton
          size="large"
          icon="single-right-arrow"
          intent="text"
          onClick={() => onClickNavigation?.('next')}
          data-test-id={
            dataTestId ? `${dataTestId}-navigate-button-next` : undefined
          }
          disabled={disabledButtons === 'next'}
        />
      </StyledNavigateButtonWrapper>
    </>
  );
};

export default Navigation;
