import React, { memo, useLayoutEffect, useMemo, useRef } from 'react';
import * as d3 from 'd3';
import { useTheme } from 'styled-components';
import { BarConfig, DataValue, YAxisConfig } from '../../type';

interface YAxisProps {
  height: number;
  yAxisConfig?: Omit<YAxisConfig, 'maxValue'>;
  dataLength: number;
  getAxisSize: (size: { width: number; height: number }) => void;
  barSize?: BarConfig<DataValue>['size'];
}

const YAxis = ({
  yAxisConfig,
  height,
  dataLength,
  getAxisSize,
  barSize = 'large',
}: YAxisProps) => {
  const theme = useTheme();

  const {
    __hd__: { barChart: barChartTheme },
  } = theme;

  const { yAxisTransformHorizontal, barMarginTop } = barChartTheme.space;

  const { barHeight: barHeightMapping } = barChartTheme.sizes;

  const barHeight = barHeightMapping[barSize];

  const YAxisRef = useRef(null);
  const labels = useMemo(
    () => yAxisConfig?.labels || [],
    [yAxisConfig?.labels]
  );
  const yScale = useMemo(() => {
    return d3
      .scaleBand<string>()
      .domain(labels as Iterable<string>)
      .range([0, height]);
  }, [labels, height]);

  useLayoutEffect(() => {
    const yAxisElement = d3.select(YAxisRef.current);
    const yAxisGenerator = d3.axisLeft(yScale).tickSize(0);

    // Because the label top is align with bar. So it can not center of the bar
    // The solution: Split the bar height to 8 parts. And spend 1 for margin top, 6 for the label text and 1 for margin bottom.
    const labelMarginTop = barHeight / 8;

    const yAxis = yAxisElement
      .append('g')
      .attr(
        'transform',
        `translate(${yAxisTransformHorizontal}, ${labelMarginTop})`
      )
      .attr('class', 'y-axis')
      .call(yAxisGenerator)
      .call((g) => g.select('.domain').remove());

    const { width: yAxisW, height: yAxisH } =
      yAxis.node()?.getBoundingClientRect() || {};

    getAxisSize({ width: yAxisW || 0, height: yAxisH || 0 });

    return () => {
      yAxis.remove();
    };
  }, [
    barHeight,
    barMarginTop,
    dataLength,
    getAxisSize,
    yAxisTransformHorizontal,
    yScale,
  ]);

  return <g ref={YAxisRef} />;
};

export default memo(YAxis);
