import React from 'react';
import renderWithTheme from '../../../../../testUtils/renderWithTheme';
import DefaultTooltip from '../DefaultTooltip';
import { theme } from '../../../../..';

describe('DefaultTooltip', () => {
  it('should render correctly', () => {
    const wrapper = renderWithTheme(
      <DefaultTooltip
        label="07th"
        legendColor={theme.colors.primary}
        value={2.2}
        percent="20"
      />
    );
    expect(wrapper.container).toMatchSnapshot();
    expect(wrapper.getByText('07th')).toBeInTheDocument();
    expect(wrapper.getByText('(20%)')).toBeInTheDocument();
    expect(wrapper.getByText('2.2')).toBeInTheDocument();
    expect(wrapper.getByTestId('legend-circle')).toHaveStyle(
      `background-color: ${theme.colors.primary}`
    );
  });
});
