import React from 'react';
import renderWithTheme from '../../../../testUtils/renderWithTheme';
import {
  StyledBarChartContainer,
  StyledLegendContainer,
  StyledNavigateButton,
  StyledNavigateButtonWrapper,
  StyledTooltipContentWrapper,
} from '../StyledHorizontalBarChart';

describe('Horizontal Bar Styled components', () => {
  it('StyledBarChartContainer', () => {
    const { container } = renderWithTheme(<StyledBarChartContainer />);
    expect(container).toMatchSnapshot();
  });

  it('StyledLegendContainer', () => {
    const { container } = renderWithTheme(<StyledLegendContainer />);
    expect(container).toMatchSnapshot();
  });

  it('StyledTooltipContentWrapper', () => {
    const { container } = renderWithTheme(<StyledTooltipContentWrapper />);
    expect(container).toMatchSnapshot();
  });

  it('StyledNavigateButton', () => {
    const { container } = renderWithTheme(
      <StyledNavigateButton icon="activate" />
    );
    expect(container).toMatchSnapshot();
  });

  it.each`
    hasTitle | position
    ${true}  | ${`left`}
    ${true}  | ${`right`}
    ${false} | ${`left`}
    ${false} | ${`right`}
  `('StyledNavigateButtonWrapper', ({ hasTitle, position }) => {
    const { container } = renderWithTheme(
      <StyledNavigateButtonWrapper
        themeIsHasTitle={hasTitle}
        themePosition={position}
        themeTop={200}
        themeLeft={0}
      />
    );
    expect(container).toMatchSnapshot();
  });
});
