import React from 'react';
import { useTheme } from 'styled-components';

type HighlightCircleProps = {
  cx?: number;
  cy?: number;
  display: React.SVGAttributes<SVGCircleElement>['display'];
  color?: string;
};

const HighlightCircle = ({ cx, cy, display, color }: HighlightCircleProps) => {
  const theme = useTheme();
  return (
    <g display={display} pointerEvents="none" data-test-id="highlight-circle">
      <circle
        cx={cx}
        cy={cy}
        r="6.5"
        stroke={color}
        strokeWidth="7"
        fill={theme.colors.defaultLightBackground}
      />
    </g>
  );
};

export default HighlightCircle;
