import React from 'react';
import Typography from '../../../Typography';
import { StyledLegendCircle } from '../../Legend/StyledLegend';

type TooltipContentProps = {
  label?: string;
  value?: number;
  legendColor?: string;
};

const TooltipContent = ({ label, value, legendColor }: TooltipContentProps) => {
  return (
    <>
      {!!legendColor && (
        <StyledLegendCircle
          themeColor={legendColor}
          data-test-id="legend-circle"
        />
      )}
      <Typography.Text
        fontSize={14}
        fontWeight="light"
        intent="subdued"
        tagName="span"
      >
        {label}
      </Typography.Text>
      <Typography.Text fontSize={14} fontWeight="light" tagName="span">
        {value}
      </Typography.Text>
    </>
  );
};

export default TooltipContent;
