import React from 'react';
import renderWithTheme from '../../../../../testUtils/renderWithTheme';
import HighlightCircle from '../HighlightCircle';
import theme from '../../../../../theme';

describe('HighlightCircle', () => {
  it('should render correctly', () => {
    const { container } = renderWithTheme(
      <HighlightCircle
        cx={10}
        cy={10}
        display="none"
        color={theme.colors.primary}
      />
    );
    expect(container).toMatchSnapshot();

    // circle element should have correct props
    expect(container.querySelector('circle')).toHaveAttribute('cx', '10');
    expect(container.querySelector('circle')).toHaveAttribute('cy', '10');
    expect(container.querySelector('circle')).toHaveAttribute(
      'stroke',
      theme.colors.primary
    );
    expect(container.querySelector('circle')).toHaveAttribute(
      'fill',
      '#ffffff'
    );

    expect(container.firstChild).toHaveAttribute('display', 'none');
    expect(container.firstChild).toHaveAttribute('pointer-events', 'none');
  });
});
