import React from 'react';
import renderWithTheme from '../../../../../testUtils/renderWithTheme';
import XAxis from '../XAxis';

describe('XAxis', () => {
  it('renders correctly', () => {
    const wrapper = renderWithTheme(
      <svg>
        <XAxis
          width={360}
          height={480}
          xAxisConfig={{
            labels: [
              'Jan',
              'Feb',
              'Mar',
              'April with long long long long long text',
              'May',
              'June',
              'July',
              'August',
              'September',
              'October',
              'November',
              'December',
            ],
          }}
        />
      </svg>
    );

    expect(wrapper.container).toMatchSnapshot();

    expect(wrapper.getByText('Jan')).toBeInTheDocument();
    expect(wrapper.getByText('Feb')).toBeInTheDocument();
    expect(wrapper.getByText('Mar')).toBeInTheDocument();
    expect(wrapper.getByText('April with lo...')).toBeInTheDocument();
  });
});
