import renderHookWithTheme from '../../../../testUtils/renderHookWithTheme';
import useSeries from '../useSeries';

describe('useSeries', () => {
  it('return correct series', () => {
    const labels = ['12'];
    const data = [
      {
        label: '1',
        data: [1],
      },
    ];
    const { result } = renderHookWithTheme(() =>
      useSeries({
        legends: ['1'],
        maxValue: 10,
        minDisplayPercentage: 4 / 100,
        labels,
        data,
      })
    );

    expect(result.current.toString()).toBe([[[0, 1]]].toString());
  });
});
