import type { FunctionComponent } from 'react';
import Checkbox from './Checkbox';
import CheckboxButton from './CheckboxButton';
import CheckboxButtonGroup from './CheckboxButtonGroup';
import CheckboxGroup from './CheckboxGroup';
import type { CheckboxProps } from './Checkbox';

interface CompoundCheckboxProps extends FunctionComponent<CheckboxProps> {
  Button: typeof CheckboxButton;
  ButtonGroup: typeof CheckboxButtonGroup;
  Group: typeof CheckboxGroup;
}

const CompoundCheckbox = Checkbox as CompoundCheckboxProps;

CompoundCheckbox.Button = CheckboxButton;
CompoundCheckbox.ButtonGroup = CheckboxButtonGroup;
CompoundCheckbox.Group = CheckboxGroup;

export default CompoundCheckbox;
