import React from 'react';
import { CommonProps } from '../common';
import { useContextPanelContext } from './ContextPanelProvider';
import { StyledContextPanelBody } from './StyledContextPanel';

export interface ContextPanelBodyProps extends CommonProps {
  children: React.ReactNode;
}

const ContextPanelBody = ({
  children,
  className,
  id,
  style,
  sx,
  'data-test-id': dataTestId = 'context-panel-body',
}: ContextPanelBodyProps) => {
  const { hasHeader, hasFooter } = useContextPanelContext();
  return (
    <StyledContextPanelBody
      themeHasFooter={hasFooter}
      themeHasHeader={hasHeader}
      className={className}
      id={id}
      style={style}
      sx={sx}
      data-test-id={dataTestId}
    >
      {children}
    </StyledContextPanelBody>
  );
};

export default ContextPanelBody;
