import React, { useEffect } from 'react';
import { CommonProps } from '../common';
import { useContextPanelContext } from './ContextPanelProvider';
import { StyledContextPanelFooter } from './StyledContextPanel';

export interface ContextPanelFooterProps extends CommonProps {
  children: React.ReactNode;
}

const ContextPanelFooter = ({
  children,
  className,
  id,
  style,
  sx,
  'data-test-id': dataTestId = 'context-panel-footer',
}: ContextPanelFooterProps) => {
  const { setHasFooter } = useContextPanelContext();

  useEffect(() => {
    setHasFooter(true);
    return () => {
      setHasFooter(false);
    };
  }, [setHasFooter]);
  return (
    <StyledContextPanelFooter
      className={className}
      id={id}
      style={style}
      sx={sx}
      data-test-id={dataTestId}
    >
      {children}
    </StyledContextPanelFooter>
  );
};

export default ContextPanelFooter;
