import React, { useEffect } from 'react';
import { CommonProps } from '../common';
import { useContextPanelContext } from './ContextPanelProvider';

import { StyledContextPanelHeader } from './StyledContextPanel';

export interface ContextPanelHeaderProps extends CommonProps {
  children: React.ReactNode;
  hasCloseButton?: boolean;
}

const ContextPanelHeader = ({
  children,
  className,
  id,
  style,
  sx,
  'data-test-id': dataTestId = 'context-panel-header',
}: ContextPanelHeaderProps) => {
  const { setHasHeader } = useContextPanelContext();

  useEffect(() => {
    setHasHeader(true);
    return () => {
      setHasHeader(false);
    };
  }, [setHasHeader]);

  return (
    <StyledContextPanelHeader
      className={className}
      id={id}
      style={style}
      sx={sx}
      data-test-id={dataTestId}
    >
      {children}
    </StyledContextPanelHeader>
  );
};

export default ContextPanelHeader;
