import { generateMonths } from '../util';

describe('utilities', () => {
  it('generateMonths with undefined params', () => {
    expect(generateMonths(0, 11)).toEqual([
      { text: 'Jan', value: 0, disabled: false },
      { text: 'Feb', value: 1, disabled: false },
      { text: 'Mar', value: 2, disabled: false },
      { text: 'Apr', value: 3, disabled: false },
      { text: 'May', value: 4, disabled: false },
      { text: 'Jun', value: 5, disabled: false },
      { text: 'Jul', value: 6, disabled: false },
      { text: 'Aug', value: 7, disabled: false },
      { text: 'Sep', value: 8, disabled: false },
      { text: 'Oct', value: 9, disabled: false },
      { text: 'Nov', value: 10, disabled: false },
      { text: 'Dec', value: 11, disabled: false },
    ]);
  });

  it('generateMonths with minMonth', () => {
    expect(generateMonths(1, 11)).toEqual([
      { text: 'Jan', value: 0, disabled: true },
      { text: 'Feb', value: 1, disabled: false },
      { text: 'Mar', value: 2, disabled: false },
      { text: 'Apr', value: 3, disabled: false },
      { text: 'May', value: 4, disabled: false },
      { text: 'Jun', value: 5, disabled: false },
      { text: 'Jul', value: 6, disabled: false },
      { text: 'Aug', value: 7, disabled: false },
      { text: 'Sep', value: 8, disabled: false },
      { text: 'Oct', value: 9, disabled: false },
      { text: 'Nov', value: 10, disabled: false },
      { text: 'Dec', value: 11, disabled: false },
    ]);
  });
  it('generateMonths with maxMonth', () => {
    expect(generateMonths(0, 10)).toEqual([
      { text: 'Jan', value: 0, disabled: false },
      { text: 'Feb', value: 1, disabled: false },
      { text: 'Mar', value: 2, disabled: false },
      { text: 'Apr', value: 3, disabled: false },
      { text: 'May', value: 4, disabled: false },
      { text: 'Jun', value: 5, disabled: false },
      { text: 'Jul', value: 6, disabled: false },
      { text: 'Aug', value: 7, disabled: false },
      { text: 'Sep', value: 8, disabled: false },
      { text: 'Oct', value: 9, disabled: false },
      { text: 'Nov', value: 10, disabled: false },
      { text: 'Dec', value: 11, disabled: true },
    ]);
  });

  it('generateMonths with minMonth and maxMonth', () => {
    expect(generateMonths(1, 10)).toEqual([
      { text: 'Jan', value: 0, disabled: true },
      { text: 'Feb', value: 1, disabled: false },
      { text: 'Mar', value: 2, disabled: false },
      { text: 'Apr', value: 3, disabled: false },
      { text: 'May', value: 4, disabled: false },
      { text: 'Jun', value: 5, disabled: false },
      { text: 'Jul', value: 6, disabled: false },
      { text: 'Aug', value: 7, disabled: false },
      { text: 'Sep', value: 8, disabled: false },
      { text: 'Oct', value: 9, disabled: false },
      { text: 'Nov', value: 10, disabled: false },
      { text: 'Dec', value: 11, disabled: true },
    ]);
  });

  it('generateMonths with LLLL format', () => {
    expect(generateMonths(0, 11, 'LLLL')).toEqual([
      { text: 'January', value: 0, disabled: false },
      { text: 'February', value: 1, disabled: false },
      { text: 'March', value: 2, disabled: false },
      { text: 'April', value: 3, disabled: false },
      { text: 'May', value: 4, disabled: false },
      { text: 'June', value: 5, disabled: false },
      { text: 'July', value: 6, disabled: false },
      { text: 'August', value: 7, disabled: false },
      { text: 'September', value: 8, disabled: false },
      { text: 'October', value: 9, disabled: false },
      { text: 'November', value: 10, disabled: false },
      { text: 'December', value: 11, disabled: false },
    ]);
  });
  it('generateMonths with format is undefined', () => {
    expect(generateMonths(0, 11)).toEqual([
      { text: 'Jan', value: 0, disabled: false },
      { text: 'Feb', value: 1, disabled: false },
      { text: 'Mar', value: 2, disabled: false },
      { text: 'Apr', value: 3, disabled: false },
      { text: 'May', value: 4, disabled: false },
      { text: 'Jun', value: 5, disabled: false },
      { text: 'Jul', value: 6, disabled: false },
      { text: 'Aug', value: 7, disabled: false },
      { text: 'Sep', value: 8, disabled: false },
      { text: 'Oct', value: 9, disabled: false },
      { text: 'Nov', value: 10, disabled: false },
      { text: 'Dec', value: 11, disabled: false },
    ]);
  });
});
