import React, { ReactElement } from 'react';

import Icon, { IconName } from '../Icon';
import { StyledDropdownButton } from './StyledFilters';
import { FilterSize } from './types';

const DropdownButton = ({
  text,
  onClick,
  active,
  leftIcon,
  rightIcon,
  'data-test-id': dataTestId,
  size: themeSize = 'medium',
}: {
  active: boolean;
  leftIcon?: IconName | ReactElement;
  onClick?: () => void;
  rightIcon?: IconName | ReactElement;
  text?: string;
  'data-test-id'?: string;
  size?: FilterSize;
}) => {
  return (
    <StyledDropdownButton
      themeSize={themeSize}
      onClick={onClick}
      themeActive={active}
      data-test-id={dataTestId}
    >
      {leftIcon !== undefined ? (
        <Icon icon={leftIcon} data-test-id="filter-icon" sx={{ mr: 'small' }} />
      ) : null}
      {text}
      {rightIcon !== undefined ? (
        <Icon icon={rightIcon} data-test-id="filter-icon" />
      ) : null}
    </StyledDropdownButton>
  );
};

export default DropdownButton;
