import React from 'react';
import renderWithTheme from '../../../testUtils/renderWithTheme';
import DropdownButton from '../DropdownButton';

describe('DropDownButton', () => {
  it.each`
    active   | size
    ${true}  | ${'small'}
    ${false} | ${'medium'}
  `('renders correctly', ({ active, size }) => {
    const { getByText } = renderWithTheme(
      <DropdownButton
        active={active}
        size={size}
        leftIcon="user"
        rightIcon="arrow-down"
        text="User"
      />
    );
    expect(getByText('User')).toBeInTheDocument();
    if (size === 'small') {
      expect(getByText('User')).toHaveStyle('font-size: 12px');
    } else {
      expect(getByText('User')).toHaveStyle('font-size: 14px');
    }

    if (active) {
      expect(getByText('User')).toHaveStyle(
        'background-color: rgb(191, 205, 223)'
      );
    } else {
      expect(getByText('User')).toHaveStyle(
        'background-color: rgb(255, 255, 255)'
      );
    }
  });
});
