import Filters from './Filters';
import Filter from './Filter';
import Group from './Group';
import Label from './Label';

type FiltersType = typeof Filters;

interface CompoundFiltersType extends FiltersType {
  Filter: typeof Filter;
  Group: typeof Group;
  Label: typeof Label;
}

const CompoundFilters = Filters as CompoundFiltersType;
CompoundFilters.Label = Label;
CompoundFilters.Filter = Filter;
CompoundFilters.Group = Group;

export default CompoundFilters;
