import type { ReactElement, FunctionComponent } from 'react';
import { INPUT_DISPLAY_NAME } from '../../Input/Input';
import type { InputProps } from '../../Input/Input';
import type { TextAreaProps } from '../../Input/TextArea';

const isInput = (
  element: ReactElement<InputProps | TextAreaProps>
): element is ReactElement<InputProps> => {
  const elementType = element.type as FunctionComponent;
  return elementType.displayName === INPUT_DISPLAY_NAME;
};

type Size = 'small' | 'medium' | 'large';

export { isInput };
export type { Size };
