import React from 'react';
import type { ReactElement, ReactNode } from 'react';
import { useCss } from '../../../utils/hooks';
import StyledGrid from './StyledGrid';
import type { CommonProps } from '../../common';
import type { Space } from '../../../theme/global/space';

type Gutter = keyof Space | 'none';

export interface GridProps extends CommonProps {
  /**
   * Grid's children node.
   */
  children?: ReactNode;
  /**
   * Space between rows, value can be none or a space value (xxsmall | small | medium | large | xlarge | xxlarge | xxxlarge | xxxxlarge), none corresponds to no space.
   */
  gutter?: Gutter;
}

const Grid = ({
  gutter = 'none',
  id,
  className,
  style,
  sx = {},
  'data-test-id': dataTestId,
  children,
}: GridProps): ReactElement => {
  return (
    <StyledGrid
      gutter={gutter}
      id={id}
      className={className}
      style={{ ...style, ...useCss(sx) }}
      data-test-id={dataTestId}
    >
      {children}
    </StyledGrid>
  );
};

export default Grid;
