import React from 'react';
import LocaleProvider from '../LocaleProvider';
import enAU from '../../locales/en_AU';
import { LocaleValues } from '../../locales/types';

export interface HeroDesignProviderProps {
  locale?: LocaleValues;
  children?: React.ReactChild;
}

const HeroDesignProvider = (props: HeroDesignProviderProps) => {
  const { locale, children } = props;
  return <LocaleProvider locale={locale || enAU}>{children}</LocaleProvider>;
};

export default HeroDesignProvider;
