import { IconButtonProps } from '../Button/IconButton';
import type { IconName, IconIntent } from '../Icon';

type IndicatorIcon = {
  icon: IconName;
  intent?: IconIntent;
};

type ActionIcon = {
  onClick: (e: React.MouseEvent<Element, MouseEvent>) => void;
  icon: IconName;
  intent?: IconButtonProps['intent'];
};

type InPageNavItem = {
  id: string;
  text: string;
  url?: string;
  disabled?: boolean;
  disabledReason?: string;
  prefix?: IndicatorIcon;
  suffix?: IndicatorIcon;
  actions?: Array<ActionIcon>;
};

export type { InPageNavItem };
