import { getThemeState } from '../utils';

describe('getThemeState', () => {
  it.each`
    disabled | invalid  | expectedState
    ${true}  | ${false} | ${'disabled'}
    ${true}  | ${true}  | ${'disabled'}
    ${false} | ${true}  | ${'invalid'}
    ${false} | ${false} | ${'enabled'}
  `('returns $expectedState', ({ disabled, invalid, expectedState }) => {
    expect(getThemeState({ disabled, invalid })).toBe(expectedState);
  });
});
