type InputState = 'enabled' | 'disabled' | 'invalid';
type Size = 'small' | 'medium' | 'large';

const getThemeState = ({
  disabled,
  invalid,
}: {
  disabled?: boolean;
  invalid?: boolean;
}): InputState => {
  if (disabled === true) {
    return 'disabled';
  }
  return invalid === true ? 'invalid' : 'enabled';
};

export { getThemeState };
export type { InputState, Size };
