import * as Locales from 'date-fns/locale';
import { Locale } from 'date-fns';
import { LocaleCode } from '../../locales/types';

export function getDateFnsLocale(locale: LocaleCode): Locale {
  const index = locale.replace('-', '');
  return (
    (Locales as { [key: string]: Locale })[index] ??
    (Locales as { [key: string]: Locale })[index.substring(0, 2)] ??
    Locales.enAU
  );
}
