import React from 'react';
import type { MouseEvent, ReactElement } from 'react';
import { useCss } from '../../utils/hooks';
import { CloseButton as StyledCloseButton } from './StyledModal';

import Icon from '../Icon';
import type { CommonProps } from '../common';

export interface ModalCloseButtonProps extends CommonProps {
  /**
   * Set the handler to handle `click` event.
   */
  onClick?: (e: MouseEvent) => void;
}

const CloseButton = ({
  onClick,
  id,
  className,
  style,
  sx = {},
  'data-test-id': dataTestId,
}: ModalCloseButtonProps): ReactElement => (
  <StyledCloseButton
    type="button"
    onClick={onClick}
    id={id}
    className={className}
    style={{ ...style, ...useCss(sx) }}
    data-test-id={dataTestId}
  >
    <Icon icon="cancel" />
  </StyledCloseButton>
);

export default CloseButton;
