import React from 'react';
import type { ReactNode, ReactElement } from 'react';
import { useCss } from '../../utils/hooks';
import { ModalFooter as StyledModalFooter } from './StyledModal';
import type { CommonProps } from '../common';

export interface ModalFooterProps extends CommonProps {
  /**
   * Modal footer content.
   */
  children: ReactNode;
}

const ModalFooter = ({
  children,
  id,
  className,
  style,
  sx = {},
  'data-test-id': dataTestId,
}: ModalFooterProps): ReactElement => (
  <StyledModalFooter
    id={id}
    className={className}
    style={{ ...style, ...useCss(sx) }}
    data-test-id={dataTestId}
  >
    {children}
  </StyledModalFooter>
);

export default ModalFooter;
