import React from 'react';
import type { ReactNode, ReactElement } from 'react';
import { useCss } from '../../utils/hooks';
import { ModalHeader as StyledModalHeader } from './StyledModal';
import ModalContext from './ModalContext';
import type { ModalContextType } from './ModalContext';
import type { CommonProps } from '../common';

export interface ModalHeaderProps extends CommonProps {
  /**
   * Modal header content.
   */
  children: ReactNode;
}

const ModalHeader = ({
  children,
  id,
  className,
  style,
  sx = {},
  'data-test-id': dataTestId,
}: ModalHeaderProps): ReactElement => {
  const { variant } = React.useContext(ModalContext) as ModalContextType;
  return (
    <StyledModalHeader
      themeVariant={variant}
      id={id}
      className={className}
      style={{ ...style, ...useCss(sx) }}
      data-test-id={dataTestId}
    >
      {children}
    </StyledModalHeader>
  );
};

export default ModalHeader;
