import type { FunctionComponent } from 'react';
import Radio from './Radio';
import RadioGroup from './RadioGroup';
import RadioButton from './RadioButton';
import RadioButtonGroup from './RadioButtonGroup';
import type { RadioProps } from './Radio';

interface CompoundRadioProps extends FunctionComponent<RadioProps> {
  Button: typeof RadioButton;
  ButtonGroup: typeof RadioButtonGroup;
  Group: typeof RadioGroup;
}

const CompoundRadio = Radio as CompoundRadioProps;

CompoundRadio.Group = RadioGroup;
CompoundRadio.Button = RadioButton;
CompoundRadio.ButtonGroup = RadioButtonGroup;

export default CompoundRadio;
