import React from 'react';

import renderWithTheme from '../../../../testUtils/renderWithTheme';

import { InputContainer } from '../../SingleSelect/Styled';

describe('InputContainer', () => {
  it.each`
    themePaddingLeft
    ${'small'}
    ${'medium'}
    ${'large'}
    ${'none'}
  `('has $themePaddingLeft padding left style', ({ themePaddingLeft }) => {
    const { container } = renderWithTheme(
      <InputContainer themePaddingLeft={themePaddingLeft} />
    );

    expect(container.firstChild).toMatchSnapshot();
  });
});
