import type { ReactElement } from 'react';
import Select from './SingleSelect';
import MultiSelect from './MultiSelect';
import type { SelectProps } from './SingleSelect';
import type { BaseOption } from './types';

type CompoundSelectComponent = (<
  V extends string | number,
  T extends BaseOption<V>
>(
  props: SelectProps<V, T>
) => ReactElement) & { Multi: typeof MultiSelect };

const CompoundSelect = Select as CompoundSelectComponent;

CompoundSelect.Multi = MultiSelect;

export default CompoundSelect;
