import React from 'react';

import {
  SelectButtonGroupWrapper,
  StyledInput,
  StyledInputText,
} from '../StyledSelectButton';
import renderWithTheme from '../../../testUtils/renderWithTheme';

describe('SelectButtonGroupWrapper', () => {
  it.each`
    themeSize
    ${'small'}
    ${'medium'}
    ${'large'}
  `('has $themeSize style', ({ themeSize }) => {
    const { container } = renderWithTheme(
      <SelectButtonGroupWrapper themeSize={themeSize} />
    );

    expect(container).toMatchSnapshot();
  });
});

describe('StyledInput', () => {
  it('has correct style', () => {
    const { container } = renderWithTheme(<StyledInput />);

    expect(container).toMatchSnapshot();
  });
});

describe('StyledInputText', () => {
  it.each`
    themeSize
    ${'small'}
    ${'medium'}
    ${'large'}
  `('has $themeSize style', ({ themeSize }) => {
    const { container } = renderWithTheme(
      <StyledInputText themeSize={themeSize} />
    );

    expect(container).toMatchSnapshot();
  });
});
