import type { ReactNode, ReactElement } from 'react';
import type { IconName } from '../Icon';

type DataAttributes = { [key: `data-${string}`]: string };

type SideBarItem = {
  id: string;
  icon: IconName | ReactElement;
  text?: string;
  url?: string;
  count?: number;
  panel?: ReactNode;
  dataAttrs?: DataAttributes;
  disabled?: boolean;
};

type PanelSubItem = {
  id: string;
  text: string;
  url: string;
  count?: number;
  dataAttrs?: DataAttributes;
};

type PanelItem = {
  id: string;
  text: string;
  url?: string;
  count?: number;
  subItems?: PanelSubItem[];
  dataAttrs?: DataAttributes;
};

export type { SideBarItem, PanelSubItem, PanelItem };
