import React from 'react';

import renderWithTheme from '../../../../testUtils/renderWithTheme';

import {
  StyledLabeledFilterContent,
  StyledDropdownButton,
  StyledDropdownContent,
  StyledLabeledFilter,
  StyledGroupedFilterContent,
} from '../StyledFilters';

describe('StyledDropdownButton', () => {
  it('matches snapshot', () => {
    const { container } = renderWithTheme(
      <>
        <StyledDropdownButton themeActive={false} />
        <StyledDropdownButton themeActive />
      </>
    );

    expect(container.firstChild).toMatchSnapshot();
  });
});

describe('StyledDropdownContent', () => {
  it('matches snapshot', () => {
    const { container } = renderWithTheme(<StyledDropdownContent />);

    expect(container.firstChild).toMatchSnapshot();
  });
});

