import React from 'react';
import OriginalFilter from './Filter';
import OriginalFilters from './Filters';
import type { FilterValues } from './types';
import type { FilterProps } from './Filter';
import type { FiltersProps } from './Filters';
import { useDeprecation } from '../../../utils/hooks';

const createFilters = <T extends FilterValues>() => {
  const Filters = (props: FiltersProps<T>) => {
    useDeprecation(
      'Table.createFilters is deprecated. Please use Filters (https://design.employmenthero.com/web/Components/Filters) instead.'
    );
    return <OriginalFilters {...props} />;
  };

  const Filter = <K extends keyof T>(props: FilterProps<T, K>) => {
    useDeprecation(
      'Table.createFilters is deprecated. Please use Filters (https://design.employmenthero.com/web/Components/Filters) instead.'
    );
    return <OriginalFilter {...props} />;
  };

  return {
    Filters,
    Filter,
  };
};

export default createFilters;
