// XXX: This needs follow typedef of react-table,
// So ban-types are required to be used in this file
/* eslint-disable @typescript-eslint/ban-types */

import {
  UseExpandedInstanceProps,
  UseExpandedOptions,
  UseExpandedRowProps,
  UseExpandedState,
  UseFiltersColumnOptions,
  UseFiltersColumnProps,
  UseFiltersInstanceProps,
  UseFiltersOptions,
  UseFiltersState,
  UseRowSelectHooks,
  UseRowSelectInstanceProps,
  UseSortByColumnOptions,
  UseSortByColumnProps,
  UseSortByHooks,
  UseSortByInstanceProps,
  UseSortByOptions,
  UseSortByState,
  UseRowSelectState,
  Accessor,
  IdType,
} from 'react-table';
import { Breakpoint } from '../../utils/responsiveBreakpoints';

declare module 'react-table' {
  export interface Hooks<D extends object = {}>
    extends UseSortByHooks<D>,
      UseRowSelectHooks<D> {}

  export interface TableInstance<D extends object = {}>
    extends UseSortByInstanceProps<D>,
      UseRowSelectInstanceProps<D>,
      UseExpandedInstanceProps<D>,
      UseFiltersInstanceProps<D> {}

  export interface ColumnInterface<D extends object = {}>
    extends UseSortByColumnOptions<D>,
      UseFiltersColumnOptions<D> {
    displayAtBreakpoint?: Breakpoint;
  }

  export interface ColumnInstance<D extends object = {}>
    extends UseSortByColumnProps<D>,
      UseFiltersColumnProps<D> {
    accessor?: keyof D extends never ? IdType<D> | Accessor<D> : Accessor<D>;
    align?: 'left' | 'right';
    width?: string;
  }

  export interface Row<D extends object = {}>
    extends UseRowSelectRowProps<D>,
      UseExpandedRowProps<D> {}

  export interface TableOptionsCustomProps<D extends object = {}> {
    customRowExpandable: (rowData: D) => boolean;
  }

  export interface TableOptions<D extends object>
    extends TableOptionsCustomProps<D>,
      UseSortByOptions<D>,
      UseExpandedOptions<D>,
      UseFiltersOptions<D> {}

  export interface TableState<D extends object = {}>
    extends UseExpandedState<D>,
      UseRowSelectState<D>,
      UseSortByState<D>,
      UseFiltersState<D> {}
}
