import React from 'react';
import renderWithTheme from '../../../testUtils/renderWithTheme';
import { TimelineItemMarker } from '../StyledTimeline';

describe('TimelineItemMarker', () => {
  it.each`
    themeIntent
    ${'success'}
    ${'info'}
    ${'warning'}
    ${'danger'}
    ${'error'}
    ${'default'}
  `('has $themeIntent style', ({ themeIntent }) => {
    const { container } = renderWithTheme(
      <TimelineItemMarker themeIntent={themeIntent} />
    );

    expect(container).toMatchSnapshot();
  });
});
