import React from 'react';

import renderWithTheme from '../../../testUtils/renderWithTheme';

import Timeline from '..';

describe('rendering', () => {
  it('shows text & helpText', () => {
    const { getByText } = renderWithTheme(
      <Timeline
        items={[
          {
            id: 1,
            text: 'Self Implementation',
            helpText:
              'Continue with your full access to all feature implementation.',
          },
          {
            id: 2,
            text: '24 January',
            helpText:
              "You trial will end. You will revert back to your plan's premium features.",
          },
        ]}
      />
    );
    expect(getByText('Self Implementation')).toBeInTheDocument();
    expect(
      getByText('Continue with your full access to all feature implementation.')
    ).toBeInTheDocument();

    expect(getByText('24 January')).toBeInTheDocument();
    expect(
      getByText(
        "You trial will end. You will revert back to your plan's premium features."
      )
    ).toBeInTheDocument();
  });
});
