import type { CSSProperties } from 'react';
import type { SxObject } from '.';

interface CommonProps {
  /**
   * A space-delimited list of class names to pass along to the element.
   */
  className?: string;
  /**
   * Testing id of the element.
   */
  'data-test-id'?: string;
  /**
   * Id of the element.
   */
  id?: string;
  /**
   * Additional inline style to be applied for the most outer element of the component.
   */
  style?: CSSProperties;
  /**
   * [Theme-aware style property](/guidelines/sxProp)
   */
  sx?: SxObject;
}

export type { CommonProps };
