interface SystemProperties {
  /**
   * Background color.
   */
  bgColor?: ThemeScales['colors'];
  /**
   * Border color.
   */
  borderColor?: ThemeScales['colors'];
  /**
   * Border radius.
   */
  borderRadius?: ThemeScales['radii'];
  /**
   * Border style.
   */
  borderStyle?: ThemeScales['borderStyles'];
  /**
   * Border width.
   */
  borderWidth?: ThemeScales['borderWidths'];
  /**
   * Box shadow.
   */
  boxShadow?: ThemeScales['shadows'];
  /**
   * Text color.
   */
  color?: ThemeScales['colors'];
  /**
   * Font size.
   */
  fontSize?: ThemeScales['fontSizes'];
  /**
   * Font weight.
   */
  fontWeight?: ThemeScales['fontWeights'];
  /**
   * Line height.
   */
  lineHeight?: ThemeScales['lineHeights'];
  /**
   * Margin.
   */
  m?: ThemeScales['space'];
  /**
   * Margin-bottom.
   */
  mb?: ThemeScales['space'];
  /**
   * Margin-left.
   */
  ml?: ThemeScales['space'];
  /**
   * Margin-right.
   */
  mr?: ThemeScales['space'];
  /**
   * Margin-top.
   */
  mt?: ThemeScales['space'];
  /**
   * Padding.
   */
  p?: ThemeScales['space'];
  /**
   * Padding-bottom.
   */
  pb?: ThemeScales['space'];
  /**
   * Padding-left.
   */
  pl?: ThemeScales['space'];
  /**
   * Padding-right.
   */
  pr?: ThemeScales['space'];
  /**
   * Padding-top.
   */
  pt?: ThemeScales['space'];
}

type SxKeys = keyof SystemProperties | keyof CSSProperties;
type SxValues = string | number;
type SxObject = Partial<Record<SxKeys, SxValues>>;

export type { ThemeScales, SystemProperties, SxKeys, SxValues, SxObject };
