export interface UnsafeArray<T> extends Array<T> {
  0: T;
  1: T;
  2: T;
  // Add more as you need, for testing purpose only
}

export const fromArray = <A>(arr: Array<A>) => arr as UnsafeArray<A>;

export const fromPromiseOfArray = <A>(promise: Promise<Array<A>>) =>
  promise as Promise<UnsafeArray<A>>;
