import type { GlobalTheme } from '../global';

const getBreadcrumbTheme = (theme: GlobalTheme) => {
  const colors = {
    item: theme.colors.subduedText,
    itemActive: theme.colors.text,
  };

  const space = {
    backIconMarginRight: `${theme.space.xsmall}px`,
    separatorMargin: `auto ${theme.space.small}px`,
  };

  const fontSizes = {
    item: `${theme.fontSizes.medium}px`,
    itemActive: `${theme.fontSizes.medium}px`,
  };

  const fontWeights = {
    item: theme.fontWeights.regular,
    itemActive: theme.fontWeights.semiBold,
  };

  const lineHeights = {
    item: `${theme.lineHeights.medium}px`,
    itemActive: `${theme.lineHeights.medium}px`,
  };

  return { colors, space, fontSizes, fontWeights, lineHeights };
};

type BreadcrumbTheme = ReturnType<typeof getBreadcrumbTheme>;

export { getBreadcrumbTheme };
export type { BreadcrumbTheme };
