import type { GlobalTheme } from '../global';

const getDividerTheme = (theme: GlobalTheme) => {
  const colors = {
    default: theme.colors.defaultBorder,
  };

  const borderWidths = {
    default: `${theme.borderWidths.base}px`,
  };

  const space = {
    xsmallMarginX: `0 ${theme.space.xsmall}px`,
    smallMarginX: `0 ${theme.space.small}px`,
    mediumMarginX: `0 ${theme.space.medium}px`,
    largeMarginX: `0 ${theme.space.large}px`,
    xlargeMarginX: `0 ${theme.space.xlarge}px`,
    xsmallMarginY: `${theme.space.xsmall}px 0`,
    smallMarginY: `${theme.space.small}px 0`,
    mediumMarginY: `${theme.space.medium}px 0`,
    largeMarginY: `${theme.space.large}px 0`,
    xlargeMarginY: `${theme.space.xlarge}px 0`,
  };

  return { colors, borderWidths, space };
};

type DividerTheme = ReturnType<typeof getDividerTheme>;

export { getDividerTheme };
export type { DividerTheme };
