import type { GlobalTheme } from '../global';

const getDropdownTheme = (theme: GlobalTheme) => {
  const colors = {
    background: theme.colors.defaultLightBackground,
  };

  const space = {
    margin: theme.space.xsmall,
  };

  const radii = {
    default: `${theme.radii.base}px`,
  };

  const shadows = {
    default: theme.shadows.default,
  };

  return { colors, space, radii, shadows };
};

type DropdownTheme = ReturnType<typeof getDropdownTheme>;

export { getDropdownTheme };
export type { DropdownTheme };
