import type { GlobalTheme } from '../global';

const getEmptyTheme = (theme: GlobalTheme) => {
  const fontSizes = {
    text: `${theme.fontSizes.medium}px`,
    title: `${theme.fontSizes.xlarge}px`,
  };

  const sizes = {
    wrapperSmallMaxWidth: '400px',
    wrapperMediumMaxWidth: '600px',
  };

  const colors = {
    text: theme.colors.bodyText,
  };

  const space = {
    wrapperPadding: `${theme.space.medium}px`,
    titleBottomMargin: `${theme.space.small}px`,
    textGroupMarginTop: `${theme.space.medium}px`,
    extraMarginTop: `${theme.space.xxxxlarge}px`,
  };

  const lineHeights = {
    text: `${theme.lineHeights.medium}px`,
    title: `${theme.lineHeights.xlarge}px`,
  };

  const fontWeights = {
    title: theme.fontWeights.bold,
  };

  return { fontSizes, sizes, colors, space, lineHeights, fontWeights };
};

type EmptyTheme = ReturnType<typeof getEmptyTheme>;

export { getEmptyTheme };
export type { EmptyTheme };
